contracts/FlashLoanArbitrage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@aave/core-v3/contracts/flashloan/base/FlashLoanSimpleReceiverBase.sol";
import "@aave/core-v3/contracts/interfaces/IPoolAddressesProvider.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// DEX Router Interfaces
interface IUniswapV3Router {
    struct ExactInputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 deadline;
        uint256 amountIn;
        uint256 amountOutMinimum;
        uint160 sqrtPriceLimitX96;
    }
    
    function exactInputSingle(ExactInputSingleParams calldata params) external payable returns (uint256 amountOut);
}

interface ISushiSwapRouter {
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
}

interface ICurvePool {
    function exchange(
        int128 i,
        int128 j,
        uint256 dx,
        uint256 min_dy
    ) external returns (uint256);
}

interface IOneInchRouter {
    struct SwapDescription {
        address srcToken;
        address dstToken;
        address srcReceiver;
        address dstReceiver;
        uint256 amount;
        uint256 minReturnAmount;
        uint256 flags;
    }
    
    function swap(
        address executor,
        SwapDescription calldata desc,
        bytes calldata permit,
        bytes calldata data
    ) external payable returns (uint256 returnAmount, uint256 spentAmount);
}

interface IBalancerVault {
    struct SingleSwap {
        bytes32 poolId;
        uint8 kind;
        address assetIn;
        address assetOut;
        uint256 amount;
        bytes userData;
    }
    
    struct FundManagement {
        address sender;
        bool fromInternalBalance;
        address payable recipient;
        bool toInternalBalance;
    }
    
    function swap(
        SingleSwap memory singleSwap,
        FundManagement memory funds,
        uint256 limit,
        uint256 deadline
    ) external payable returns (uint256);
}

/**
 * @title FlashLoanArbitrage
 * @author Arbitrage Bot System
 * @notice Ana flashloan arbitraj kontratı - Aave V3 kullanarak multi-DEX arbitraj yapar
 * @dev Kurumsal seviye güvenlik önlemleri ve optimizasyonlar içerir
 */
contract FlashLoanArbitrage is FlashLoanSimpleReceiverBase, Ownable2Step, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;
    
    // ============ Constants ============
    uint256 private constant MAX_SLIPPAGE = 300; // 3% max slippage (basis points)
    uint256 private constant MIN_PROFIT_BASIS_POINTS = 30; // 0.3% minimum profit
    uint256 private constant BASIS_POINTS = 10000;
    
    // ============ State Variables ============
    
    // DEX Router addresses
    IUniswapV3Router public immutable uniswapV3Router;
    ISushiSwapRouter public immutable sushiSwapRouter;
    IOneInchRouter public immutable oneInchRouter;
    IBalancerVault public immutable balancerVault;
    
    // Curve pools mapping
    mapping(address => mapping(address => address)) public curvePools; // token0 => token1 => pool
    
    // Security & Risk Management
    mapping(address => bool) public authorizedCallers;
    mapping(address => uint256) public maxFlashLoanAmount;
    uint256 public maxGasPrice = 150 gwei;
    
    // Profit tracking
    mapping(address => uint256) public totalProfitByToken;
    uint256 public totalArbitrageCount;
    uint256 public successfulArbitrageCount;
    
    // Circuit breaker
    uint256 public maxDailyLoss = 1 ether; // Max 1 ETH loss per day
    uint256 public currentDayLoss;
    uint256 public lastResetTimestamp;
    
    // ============ Events ============
    event ArbitrageExecuted(
        address indexed token,
        uint256 amount,
        uint256 profit,
        string[] dexPath,
        uint256 gasUsed
    );
    
    event ArbitrageFailed(
        address indexed token,
        uint256 amount,
        string reason
    );
    
    event CircuitBreakerTriggered(uint256 totalLoss);
    event AuthorizedCallerUpdated(address indexed caller, bool authorized);
    event MaxFlashLoanAmountUpdated(address indexed token, uint256 amount);
    event CurvePoolUpdated(address indexed token0, address indexed token1, address pool);
    
    // ============ Errors ============
    error UnauthorizedCaller();
    error InvalidParameters();
    error InsufficientProfit();
    error ExcessiveGasPrice();
    error MaxLossExceeded();
    error FlashLoanFailed();
    error SwapFailed();
    
    // ============ Modifiers ============
    modifier onlyAuthorized() {
        if (!authorizedCallers[msg.sender] && msg.sender != owner()) {
            revert UnauthorizedCaller();
        }
        _;
    }
    
    modifier checkGasPrice() {
        if (tx.gasprice > maxGasPrice) {
            revert ExcessiveGasPrice();
        }
        _;
    }
    
    modifier resetDailyLossIfNeeded() {
        if (block.timestamp > lastResetTimestamp + 1 days) {
            currentDayLoss = 0;
            lastResetTimestamp = block.timestamp;
        }
        _;
    }
    
    // ============ Constructor ============
    constructor(
        address _addressProvider,
        address _uniswapV3Router,
        address _sushiSwapRouter,
        address _oneInchRouter,
        address _balancerVault
    ) FlashLoanSimpleReceiverBase(IPoolAddressesProvider(_addressProvider)) Ownable(msg.sender) {
        uniswapV3Router = IUniswapV3Router(_uniswapV3Router);
        sushiSwapRouter = ISushiSwapRouter(_sushiSwapRouter);
        oneInchRouter = IOneInchRouter(_oneInchRouter);
        balancerVault = IBalancerVault(_balancerVault);
        
        lastResetTimestamp = block.timestamp;
        authorizedCallers[msg.sender] = true;
    }
    
    // ============ External Functions ============
    
    /**
     * @notice Ana arbitraj fonksiyonu - flashloan başlatır
     * @param asset Arbitraj yapılacak token adresi
     * @param amount Flashloan miktarı
     * @param params Arbitraj parametreleri (encoded)
     */
    function executeArbitrage(
        address asset,
        uint256 amount,
        bytes calldata params
    ) external onlyAuthorized checkGasPrice whenNotPaused resetDailyLossIfNeeded {
        // Validate parameters
        if (asset == address(0) || amount == 0) {
            revert InvalidParameters();
        }
        
        // Check max flashloan amount
        if (maxFlashLoanAmount[asset] > 0 && amount > maxFlashLoanAmount[asset]) {
            revert InvalidParameters();
        }
        
        totalArbitrageCount++;
        
        // Request flashloan from Aave
        POOL.flashLoanSimple(
            address(this),
            asset,
            amount,
            params,
            0 // referral code
        );
    }
    
    /**
     * @notice Aave flashloan callback
     * @dev Bu fonksiyon sadece Aave Pool tarafından çağrılabilir
     */
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external override returns (bool) {
        // Verify the flashloan was initiated by this contract
        if (initiator != address(this)) {
            revert FlashLoanFailed();
        }
        
        // Verify the caller is the Aave pool
        if (msg.sender != address(POOL)) {
            revert UnauthorizedCaller();
        }
        
        uint256 initialBalance = IERC20(asset).balanceOf(address(this));
        
        try this._executeArbitrageInternal(asset, amount, params) returns (uint256 finalBalance) {
            // Calculate profit
            uint256 totalDebt = amount + premium;
            
            if (finalBalance < totalDebt) {
                // Loss occurred
                uint256 loss = totalDebt - finalBalance;
                currentDayLoss += loss;
                
                if (currentDayLoss > maxDailyLoss) {
                    _pause(); // Circuit breaker
                    emit CircuitBreakerTriggered(currentDayLoss);
                }
                
                emit ArbitrageFailed(asset, amount, "Insufficient returns");
                revert InsufficientProfit();
            }
            
            uint256 profit = finalBalance - totalDebt;
            
            // Check minimum profit requirement
            uint256 minProfit = (amount * MIN_PROFIT_BASIS_POINTS) / BASIS_POINTS;
            if (profit < minProfit) {
                emit ArbitrageFailed(asset, amount, "Below minimum profit threshold");
                revert InsufficientProfit();
            }
            
            // Track profit
            totalProfitByToken[asset] += profit;
            successfulArbitrageCount++;
            
            // Approve Aave for repayment
            IERC20(asset).forceApprove(address(POOL), totalDebt);
            
            // Extract decoded path for event
            (string[] memory dexPath, , , ) = abi.decode(params, (string[], uint256[], address[], bytes[]));
            
            emit ArbitrageExecuted(
                asset,
                amount,
                profit,
                dexPath,
                initialBalance // Using as proxy for gas used
            );
            
            return true;
            
        } catch Error(string memory reason) {
            emit ArbitrageFailed(asset, amount, reason);
            revert SwapFailed();
        } catch {
            emit ArbitrageFailed(asset, amount, "Unknown error");
            revert SwapFailed();
        }
    }
    
    /**
     * @notice Internal arbitrage execution logic
     * @dev External call to enable try-catch error handling
     */
    function _executeArbitrageInternal(
        address asset,
        uint256 amount,
        bytes calldata params
    ) external returns (uint256) {
        // Only callable by this contract
        if (msg.sender != address(this)) {
            revert UnauthorizedCaller();
        }
        
        // Decode arbitrage path
        (
            string[] memory dexPath,
            uint256[] memory amounts,
            address[] memory tokens,
            bytes[] memory swapData
        ) = abi.decode(params, (string[], uint256[], address[], bytes[]));
        
        // Validate path
        if (dexPath.length == 0 || dexPath.length != swapData.length) {
            revert InvalidParameters();
        }
        
        // Execute swaps
        uint256 currentAmount = amount;
        address currentToken = asset;
        
        for (uint256 i = 0; i < dexPath.length; i++) {
            address nextToken = tokens[i];
            uint256 minAmountOut = amounts[i];
            
            // Apply slippage protection
            uint256 slippageAdjustedMin = (minAmountOut * (BASIS_POINTS - MAX_SLIPPAGE)) / BASIS_POINTS;
            
            currentAmount = _executeDexSwap(
                dexPath[i],
                currentToken,
                nextToken,
                currentAmount,
                slippageAdjustedMin,
                swapData[i]
            );
            
            currentToken = nextToken;
        }
        
        // Verify we ended with the original asset
        if (currentToken != asset) {
            revert InvalidParameters();
        }
        
        return currentAmount;
    }
    
    /**
     * @notice Execute swap on specific DEX
     */
    function _executeDexSwap(
        string memory dexName,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        bytes memory swapData
    ) private returns (uint256 amountOut) {
        // Approve the respective router
        if (keccak256(bytes(dexName)) == keccak256(bytes("UniswapV3"))) {
            IERC20(tokenIn).forceApprove(address(uniswapV3Router), amountIn);
            
            (uint24 fee, uint160 sqrtPriceLimitX96) = abi.decode(swapData, (uint24, uint160));
            
            IUniswapV3Router.ExactInputSingleParams memory params = IUniswapV3Router.ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: fee,
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: amountIn,
                amountOutMinimum: minAmountOut,
                sqrtPriceLimitX96: sqrtPriceLimitX96
            });
            
            amountOut = uniswapV3Router.exactInputSingle(params);
            
        } else if (keccak256(bytes(dexName)) == keccak256(bytes("SushiSwap"))) {
            IERC20(tokenIn).forceApprove(address(sushiSwapRouter), amountIn);
            
            address[] memory path = new address[](2);
            path[0] = tokenIn;
            path[1] = tokenOut;
            
            uint[] memory amounts = sushiSwapRouter.swapExactTokensForTokens(
                amountIn,
                minAmountOut,
                path,
                address(this),
                block.timestamp
            );
            
            amountOut = amounts[amounts.length - 1];
            
        } else if (keccak256(bytes(dexName)) == keccak256(bytes("Curve"))) {
            address pool = curvePools[tokenIn][tokenOut];
            if (pool == address(0)) {
                revert InvalidParameters();
            }
            
            IERC20(tokenIn).forceApprove(pool, amountIn);
            
            (int128 i, int128 j) = abi.decode(swapData, (int128, int128));
            
            amountOut = ICurvePool(pool).exchange(i, j, amountIn, minAmountOut);
            
        } else if (keccak256(bytes(dexName)) == keccak256(bytes("1inch"))) {
            IERC20(tokenIn).forceApprove(address(oneInchRouter), amountIn);
            
            (address executor, bytes memory permitData, bytes memory executorData) = 
                abi.decode(swapData, (address, bytes, bytes));
            
            IOneInchRouter.SwapDescription memory desc = IOneInchRouter.SwapDescription({
                srcToken: tokenIn,
                dstToken: tokenOut,
                srcReceiver: executor,
                dstReceiver: address(this),
                amount: amountIn,
                minReturnAmount: minAmountOut,
                flags: 0
            });
            
            (amountOut, ) = oneInchRouter.swap(executor, desc, permitData, executorData);
            
        } else if (keccak256(bytes(dexName)) == keccak256(bytes("Balancer"))) {
            IERC20(tokenIn).forceApprove(address(balancerVault), amountIn);
            
            (bytes32 poolId, bytes memory userData) = abi.decode(swapData, (bytes32, bytes));
            
            IBalancerVault.SingleSwap memory singleSwap = IBalancerVault.SingleSwap({
                poolId: poolId,
                kind: 0, // GIVEN_IN
                assetIn: tokenIn,
                assetOut: tokenOut,
                amount: amountIn,
                userData: userData
            });
            
            IBalancerVault.FundManagement memory funds = IBalancerVault.FundManagement({
                sender: address(this),
                fromInternalBalance: false,
                recipient: payable(address(this)),
                toInternalBalance: false
            });
            
            amountOut = balancerVault.swap(singleSwap, funds, minAmountOut, block.timestamp);
        } else {
            revert InvalidParameters();
        }
        
        // Verify we got at least the minimum amount
        if (amountOut < minAmountOut) {
            revert InsufficientProfit();
        }
        
        return amountOut;
    }
    
    // ============ Admin Functions ============
    
    /**
     * @notice Update authorized caller status
     */
    function setAuthorizedCaller(address caller, bool authorized) external onlyOwner {
        authorizedCallers[caller] = authorized;
        emit AuthorizedCallerUpdated(caller, authorized);
    }
    
    /**
     * @notice Set maximum flashloan amount for a token
     */
    function setMaxFlashLoanAmount(address token, uint256 amount) external onlyOwner {
        maxFlashLoanAmount[token] = amount;
        emit MaxFlashLoanAmountUpdated(token, amount);
    }
    
    /**
     * @notice Update Curve pool address
     */
    function setCurvePool(address token0, address token1, address pool) external onlyOwner {
        curvePools[token0][token1] = pool;
        curvePools[token1][token0] = pool; // Bidirectional
        emit CurvePoolUpdated(token0, token1, pool);
    }
    
    /**
     * @notice Update max gas price
     */
    function setMaxGasPrice(uint256 _maxGasPrice) external onlyOwner {
        maxGasPrice = _maxGasPrice;
    }
    
    /**
     * @notice Update max daily loss
     */
    function setMaxDailyLoss(uint256 _maxDailyLoss) external onlyOwner {
        maxDailyLoss = _maxDailyLoss;
    }
    
    /**
     * @notice Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @notice Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    /**
     * @notice Emergency withdraw
     */
    function emergencyWithdraw(address token) external onlyOwner {
        uint256 balance = IERC20(token).balanceOf(address(this));
        if (balance > 0) {
            IERC20(token).safeTransfer(owner(), balance);
        }
    }
    
    /**
     * @notice Withdraw profits
     */
    function withdrawProfits(address token, uint256 amount) external onlyOwner {
        uint256 balance = IERC20(token).balanceOf(address(this));
        if (amount > balance) {
            revert InvalidParameters();
        }
        IERC20(token).safeTransfer(owner(), amount);
    }
    
    // ============ View Functions ============
    
    /**
     * @notice Get contract statistics
     */
    function getStatistics() external view returns (
        uint256 totalCount,
        uint256 successCount,
        uint256 dailyLoss,
        bool isPaused
    ) {
        return (
            totalArbitrageCount,
            successfulArbitrageCount,
            currentDayLoss,
            paused()
        );
    }
    
    /**
     * @notice Check if an address is authorized
     */
    function isAuthorized(address caller) external view returns (bool) {
        return authorizedCallers[caller] || caller == owner();
    }
    
    // ============ Receive Ether ============
    receive() external payable {}
}

contracts/interfaces/IAaveV3.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title IAaveV3
 * @author Arbitrage Bot System
 * @notice Aave V3 protokolü ile etkileşim için gerekli tüm interface tanımlamaları
 * @dev Bu dosya Aave V3'ün core interface'lerini içerir
 */

// ============ Core Interfaces ============

/**
 * @title IPool
 * @notice Aave V3 Pool ana interface'i
 * @dev Flashloan ve temel lending işlemleri için kullanılır
 */
interface IPool {
    /**
     * @notice Flashloan parametreleri için event
     */
    event FlashLoan(
        address indexed target,
        address indexed initiator,
        address indexed asset,
        uint256 amount,
        uint256 premium,
        uint16 referralCode
    );
    
    /**
     * @notice Basit flashloan fonksiyonu (tek asset)
     * @param receiverAddress Flashloan'ı alacak kontrat adresi
     * @param asset Ödünç alınacak token adresi
     * @param amount Ödünç alınacak miktar
     * @param params Callback'e gönderilecek parametreler
     * @param referralCode Referans kodu (genelde 0)
     */
    function flashLoanSimple(
        address receiverAddress,
        address asset,
        uint256 amount,
        bytes calldata params,
        uint16 referralCode
    ) external;
    
    /**
     * @notice Çoklu asset flashloan fonksiyonu
     * @param receiverAddress Flashloan'ı alacak kontrat adresi
     * @param assets Ödünç alınacak token adresleri array'i
     * @param amounts Her token için ödünç alınacak miktarlar
     * @param modes Flash loan modları (0 = flash loan olarak geri öde)
     * @param onBehalfOf Borç kimin adına alınacak (flash loan için address(0))
     * @param params Callback'e gönderilecek parametreler
     * @param referralCode Referans kodu
     */
    function flashLoan(
        address receiverAddress,
        address[] calldata assets,
        uint256[] calldata amounts,
        uint256[] calldata modes,
        address onBehalfOf,
        bytes calldata params,
        uint16 referralCode
    ) external;
    
    /**
     * @notice Get reserve data from the pool
     * @param asset Token address
     * @return configuration The configuration bitmap
     * @return liquidityIndex The liquidity index
     * @return variableBorrowIndex The variable borrow index
     * @return currentLiquidityRate The current liquidity rate
     * @return currentVariableBorrowRate The current variable borrow rate
     * @return currentStableBorrowRate The current stable borrow rate
     * @return lastUpdateTimestamp The timestamp of the last update
     * @return aTokenAddress The address of the aToken
     * @return stableDebtTokenAddress The address of the stable debt token
     * @return variableDebtTokenAddress The address of the variable debt token
     * @return interestRateStrategyAddress The address of the interest rate strategy
     * @return id The reserve id
     */
    function getReserveData(address asset) external view returns (
        uint256 configuration,
        uint128 liquidityIndex,
        uint128 variableBorrowIndex,
        uint128 currentLiquidityRate,
        uint128 currentVariableBorrowRate,
        uint128 currentStableBorrowRate,
        uint40 lastUpdateTimestamp,
        address aTokenAddress,
        address stableDebtTokenAddress,
        address variableDebtTokenAddress,
        address interestRateStrategyAddress,
        uint8 id
    );
    
    /**
     * @notice Kullanıcı verisini getir
     */
    function getUserAccountData(address user) external view returns (
        uint256 totalCollateralETH,
        uint256 totalDebtETH,
        uint256 availableBorrowsETH,
        uint256 currentLiquidationThreshold,
        uint256 ltv,
        uint256 healthFactor
    );
    
    /**
     * @notice Supply (deposit) fonksiyonu
     */
    function supply(
        address asset,
        uint256 amount,
        address onBehalfOf,
        uint16 referralCode
    ) external;
    
    /**
     * @notice Withdraw fonksiyonu
     */
    function withdraw(
        address asset,
        uint256 amount,
        address to
    ) external returns (uint256);
    
    /**
     * @notice Borrow fonksiyonu
     */
    function borrow(
        address asset,
        uint256 amount,
        uint256 interestRateMode,
        uint16 referralCode,
        address onBehalfOf
    ) external;
    
    /**
     * @notice Repay fonksiyonu
     */
    function repay(
        address asset,
        uint256 amount,
        uint256 rateMode,
        address onBehalfOf
    ) external returns (uint256);
}

/**
 * @title IFlashLoanSimpleReceiver
 * @notice Basit flashloan receiver interface'i
 * @dev FlashLoanArbitrage kontratımız bu interface'i implement eder
 */
interface IFlashLoanSimpleReceiver {
    /**
     * @notice Aave Pool tarafından flashloan sonrası çağrılan fonksiyon
     * @param asset Ödünç alınan token
     * @param amount Ödünç alınan miktar
     * @param premium Ödenecek ücret
     * @param initiator Flashloan'ı başlatan adres
     * @param params İşlem parametreleri
     * @return True döndürmeli, aksi halde işlem revert olur
     */
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external returns (bool);
}

/**
 * @title IFlashLoanReceiver
 * @notice Çoklu asset flashloan receiver interface'i
 */
interface IFlashLoanReceiver {
    /**
     * @notice Çoklu asset flashloan callback fonksiyonu
     */
    function executeOperation(
        address[] calldata assets,
        uint256[] calldata amounts,
        uint256[] calldata premiums,
        address initiator,
        bytes calldata params
    ) external returns (bool);
}

/**
 * @title IPoolAddressesProvider
 * @notice Aave V3 adres provider interface'i
 * @dev Pool ve diğer core kontratların adreslerini sağlar
 */
interface IPoolAddressesProvider {
    /**
     * @notice Market ID'sini döndürür
     */
    function getMarketId() external view returns (string memory);
    
    /**
     * @notice Pool adresini döndürür
     */
    function getPool() external view returns (address);
    
    /**
     * @notice Pool'u güncelle
     */
    function setPoolImpl(address pool) external;
    
    /**
     * @notice Pool Configurator adresini döndürür
     */
    function getPoolConfigurator() external view returns (address);
    
    /**
     * @notice Price Oracle adresini döndürür
     */
    function getPriceOracle() external view returns (address);
    
    /**
     * @notice ACL Manager adresini döndürür
     */
    function getACLManager() external view returns (address);
    
    /**
     * @notice ACL Admin adresini döndürür
     */
    function getACLAdmin() external view returns (address);
    
    /**
     * @notice Pool Data Provider adresini döndürür
     */
    function getPoolDataProvider() external view returns (address);
}

/**
 * @title IPoolDataProvider
 * @notice Aave V3 Pool data provider interface'i
 * @dev Detaylı pool ve reserve verileri için kullanılır
 */
interface IPoolDataProvider {
    /**
     * @notice Tüm reserve'lerin listesini döndürür
     */
    function getAllReservesTokens() external view returns (TokenData[] memory);
    
    /**
     * @notice Tüm aToken'ların listesini döndürür
     */
    function getAllATokens() external view returns (TokenData[] memory);
    
    /**
     * @notice Reserve configuration verilerini döndürür
     */
    function getReserveConfigurationData(address asset) external view returns (
        uint256 decimals,
        uint256 ltv,
        uint256 liquidationThreshold,
        uint256 liquidationBonus,
        uint256 reserveFactor,
        bool usageAsCollateralEnabled,
        bool borrowingEnabled,
        bool stableBorrowRateEnabled,
        bool isActive,
        bool isFrozen
    );
    
    /**
     * @notice Reserve caps (limits) verilerini döndürür
     */
    function getReserveCaps(address asset) external view returns (
        uint256 borrowCap,
        uint256 supplyCap
    );
    
    /**
     * @notice Kullanıcının reserve verilerini döndürür
     */
    function getUserReserveData(address asset, address user) external view returns (
        uint256 currentATokenBalance,
        uint256 currentStableDebt,
        uint256 currentVariableDebt,
        uint256 principalStableDebt,
        uint256 scaledVariableDebt,
        uint256 stableBorrowRate,
        uint256 liquidityRate,
        uint40 stableRateLastUpdated,
        bool usageAsCollateralEnabled
    );
    
    /**
     * @notice Token data struct
     */
    struct TokenData {
        string symbol;
        address tokenAddress;
    }
}

/**
 * @title IPriceOracle
 * @notice Aave V3 Price Oracle interface'i
 * @dev Asset fiyatlarını almak için kullanılır
 */
interface IPriceOracle {
    /**
     * @notice Asset'in ETH cinsinden fiyatını döndürür
     * @param asset Token adresi
     * @return Fiyat (WEI cinsinden)
     */
    function getAssetPrice(address asset) external view returns (uint256);
    
    /**
     * @notice Birden fazla asset'in fiyatını döndürür
     */
    function getAssetsPrices(address[] calldata assets) external view returns (uint256[] memory);
    
    /**
     * @notice Fiyat kaynağını döndürür
     */
    function getSourceOfAsset(address asset) external view returns (address);
    
    /**
     * @notice Fallback oracle'ı döndürür
     */
    function getFallbackOracle() external view returns (address);
}

/**
 * @title IACLManager
 * @notice Access Control List Manager interface'i
 * @dev Yetkilendirme ve rol yönetimi için kullanılır
 */
interface IACLManager {
    /**
     * @notice Pool admin rolünü kontrol eder
     */
    function isPoolAdmin(address admin) external view returns (bool);
    
    /**
     * @notice Emergency admin rolünü kontrol eder
     */
    function isEmergencyAdmin(address admin) external view returns (bool);
    
    /**
     * @notice Risk admin rolünü kontrol eder
     */
    function isRiskAdmin(address admin) external view returns (bool);
    
    /**
     * @notice Flash borrower rolünü kontrol eder
     */
    function isFlashBorrower(address borrower) external view returns (bool);
    
    /**
     * @notice Bridge rolünü kontrol eder
     */
    function isBridge(address bridge) external view returns (bool);
    
    /**
     * @notice Asset listing admin rolünü kontrol eder
     */
    function isAssetListingAdmin(address admin) external view returns (bool);
}

/**
 * @title IPoolConfigurator
 * @notice Pool configuration interface'i
 * @dev Reserve parametrelerini ayarlamak için kullanılır (admin only)
 */
interface IPoolConfigurator {
    /**
     * @notice Reserve'ü başlatır
     */
    function initReserves(ConfigureReserveInput[] calldata input) external;
    
    /**
     * @notice Borrow cap'i günceller
     */
    function setBorrowCap(address asset, uint256 borrowCap) external;
    
    /**
     * @notice Supply cap'i günceller
     */
    function setSupplyCap(address asset, uint256 supplyCap) external;
    
    /**
     * @notice Liquidation bonus'u günceller
     */
    function setLiquidationBonus(address asset, uint256 bonus) external;
    
    /**
     * @notice Reserve'ü freeze/unfreeze eder
     */
    function setReserveFreeze(address asset, bool freeze) external;
    
    /**
     * @notice Borrowing'i enable/disable eder
     */
    function setReserveBorrowing(address asset, bool enabled) external;
    
    /**
     * @notice Reserve'ü active/inactive yapar
     */
    function setReserveActive(address asset, bool active) external;
    
    /**
     * @notice Reserve factor'ü günceller
     */
    function setReserveFactor(address asset, uint256 reserveFactor) external;
    
    /**
     * @notice Configure reserve input struct
     */
    struct ConfigureReserveInput {
        address aTokenImpl;
        address stableDebtTokenImpl;
        address variableDebtTokenImpl;
        uint8 underlyingAssetDecimals;
        address interestRateStrategyAddress;
        address underlyingAsset;
        address treasury;
        address incentivesController;
        string aTokenName;
        string aTokenSymbol;
        string variableDebtTokenName;
        string variableDebtTokenSymbol;
        string stableDebtTokenName;
        string stableDebtTokenSymbol;
        bytes params;
    }
}

/**
 * @title DataTypes
 * @notice Aave V3'te kullanılan veri tipleri
 */
library DataTypes {
    struct ReserveData {
        ReserveConfigurationMap configuration;
        uint128 liquidityIndex;
        uint128 variableBorrowIndex;
        uint128 currentLiquidityRate;
        uint128 currentVariableBorrowRate;
        uint128 currentStableBorrowRate;
        uint40 lastUpdateTimestamp;
        address aTokenAddress;
        address stableDebtTokenAddress;
        address variableDebtTokenAddress;
        address interestRateStrategyAddress;
        uint8 id;
    }
    
    struct ReserveConfigurationMap {
        uint256 data;
    }
    
    struct UserConfigurationMap {
        uint256 data;
    }
    
    enum InterestRateMode {
        NONE,
        STABLE,
        VARIABLE
    }
}

/**
 * @title FlashLoanSimpleReceiverBase
 * @notice Flashloan receiver base contract
 * @dev Bu abstract contract'ı FlashLoanArbitrage.sol import ediyor
 */
abstract contract FlashLoanSimpleReceiverBase is IFlashLoanSimpleReceiver {
    IPoolAddressesProvider public immutable ADDRESSES_PROVIDER;
    IPool public immutable POOL;
    
    constructor(IPoolAddressesProvider provider) {
        ADDRESSES_PROVIDER = provider;
        POOL = IPool(provider.getPool());
    }
}

contracts/interfaces/IDEXRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title IDEXRouter
 * @author Arbitrage Bot System
 * @notice Tüm DEX protokolleri için router interface tanımlamaları
 * @dev Bu dosya Uniswap V3, Sushiswap, Curve, 1inch ve Balancer interface'lerini içerir
 */

// ============ Uniswap V3 Interfaces ============

/**
 * @title IUniswapV3Factory
 * @notice Uniswap V3 Factory interface'i
 * @dev Pool oluşturma ve pool adresi sorgulama için kullanılır
 */
interface IUniswapV3Factory {
    /**
     * @notice Pool oluşturulduğunda emit edilir
     */
    event PoolCreated(
        address indexed token0,
        address indexed token1,
        uint24 indexed fee,
        int24 tickSpacing,
        address pool
    );

    /**
     * @notice Belirli bir token çifti ve fee için pool adresini döndürür
     * @param tokenA İlk token
     * @param tokenB İkinci token
     * @param fee Pool fee tier (500, 3000, 10000)
     * @return pool Pool adresi
     */
    function getPool(
        address tokenA,
        address tokenB,
        uint24 fee
    ) external view returns (address pool);

    /**
     * @notice Yeni bir pool oluşturur
     */
    function createPool(
        address tokenA,
        address tokenB,
        uint24 fee
    ) external returns (address pool);

    /**
     * @notice Fee amount için tick spacing'i döndürür
     */
    function feeAmountTickSpacing(uint24 fee) external view returns (int24);
}

/**
 * @title IUniswapV3SwapRouter
 * @notice Uniswap V3 SwapRouter02 interface'i
 * @dev Token swap işlemleri için kullanılır
 */
interface IUniswapV3SwapRouter {
    /**
     * @notice Tek havuzdan exact input swap parametreleri
     */
    struct ExactInputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 deadline;
        uint256 amountIn;
        uint256 amountOutMinimum;
        uint160 sqrtPriceLimitX96;
    }

    /**
     * @notice Çoklu havuzdan exact input swap parametreleri
     */
    struct ExactInputParams {
        bytes path;
        address recipient;
        uint256 deadline;
        uint256 amountIn;
        uint256 amountOutMinimum;
    }

    /**
     * @notice Tek havuzdan exact output swap parametreleri
     */
    struct ExactOutputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 deadline;
        uint256 amountOut;
        uint256 amountInMaximum;
        uint160 sqrtPriceLimitX96;
    }

    /**
     * @notice Tek havuzdan exact input swap
     */
    function exactInputSingle(ExactInputSingleParams calldata params)
        external
        payable
        returns (uint256 amountOut);

    /**
     * @notice Çoklu havuzdan exact input swap
     */
    function exactInput(ExactInputParams calldata params)
        external
        payable
        returns (uint256 amountOut);

    /**
     * @notice Tek havuzdan exact output swap
     */
    function exactOutputSingle(ExactOutputSingleParams calldata params)
        external
        payable
        returns (uint256 amountIn);

    /**
     * @notice Multicall - birden fazla fonksiyonu tek transaction'da çağır
     */
    function multicall(bytes[] calldata data)
        external
        payable
        returns (bytes[] memory results);

    /**
     * @notice Kullanılmayan ETH'yi geri gönder
     */
    function refundETH() external payable;

    /**
     * @notice Token'ları çek
     */
    function sweepToken(
        address token,
        uint256 amountMinimum,
        address recipient
    ) external payable;

    /**
     * @notice WETH'i unwrap et
     */
    function unwrapWETH9(uint256 amountMinimum, address recipient) external payable;
}

/**
 * @title IUniswapV3Quoter
 * @notice Uniswap V3 Quoter interface'i
 * @dev Swap simülasyonu ve fiyat tahmini için kullanılır
 */
interface IUniswapV3Quoter {
    /**
     * @notice Exact input single quote
     */
    function quoteExactInputSingle(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn,
        uint160 sqrtPriceLimitX96
    ) external returns (uint256 amountOut);

    /**
     * @notice Exact input multi-hop quote
     */
    function quoteExactInput(bytes memory path, uint256 amountIn)
        external
        returns (uint256 amountOut);

    /**
     * @notice Exact output single quote
     */
    function quoteExactOutputSingle(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountOut,
        uint160 sqrtPriceLimitX96
    ) external returns (uint256 amountIn);
}

/**
 * @title IUniswapV3Pool
 * @notice Uniswap V3 Pool interface'i
 * @dev Pool state ve liquidity bilgileri için kullanılır
 */
interface IUniswapV3Pool {
    /**
     * @notice Pool'un mevcut slot0 verilerini döndürür
     */
    function slot0()
        external
        view
        returns (
            uint160 sqrtPriceX96,
            int24 tick,
            uint16 observationIndex,
            uint16 observationCardinality,
            uint16 observationCardinalityNext,
            uint8 feeProtocol,
            bool unlocked
        );

    /**
     * @notice Pool'daki likiditeyi döndürür
     */
    function liquidity() external view returns (uint128);

    /**
     * @notice Token0 adresini döndürür
     */
    function token0() external view returns (address);

    /**
     * @notice Token1 adresini döndürür
     */
    function token1() external view returns (address);

    /**
     * @notice Pool fee'sini döndürür
     */
    function fee() external view returns (uint24);

    /**
     * @notice Tick spacing'i döndürür
     */
    function tickSpacing() external view returns (int24);

    /**
     * @notice Swap fonksiyonu
     */
    function swap(
        address recipient,
        bool zeroForOne,
        int256 amountSpecified,
        uint160 sqrtPriceLimitX96,
        bytes calldata data
    ) external returns (int256 amount0, int256 amount1);

    /**
     * @notice Pozisyon bilgisi
     */
    function positions(bytes32 key)
        external
        view
        returns (
            uint128 liquidity,
            uint256 feeGrowthInside0LastX128,
            uint256 feeGrowthInside1LastX128,
            uint128 tokensOwed0,
            uint128 tokensOwed1
        );

    /**
     * @notice Observation bilgisi
     */
    function observations(uint256 index)
        external
        view
        returns (
            uint32 blockTimestamp,
            int56 tickCumulative,
            uint160 secondsPerLiquidityCumulativeX128,
            bool initialized
        );
}

// ============ SushiSwap Interfaces ============

/**
 * @title ISushiSwapV2Factory
 * @notice SushiSwap V2 Factory interface'i
 */
interface ISushiSwapV2Factory {
    event PairCreated(address indexed token0, address indexed token1, address pair, uint);

    function getPair(address tokenA, address tokenB) external view returns (address pair);
    function allPairs(uint) external view returns (address pair);
    function allPairsLength() external view returns (uint);
    function createPair(address tokenA, address tokenB) external returns (address pair);
    function feeTo() external view returns (address);
    function feeToSetter() external view returns (address);
}

/**
 * @title ISushiSwapV2Router
 * @notice SushiSwap V2 Router interface'i
 */
interface ISushiSwapV2Router {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    /**
     * @notice Add liquidity
     */
    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);

    /**
     * @notice Add liquidity ETH
     */
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);

    /**
     * @notice Swap exact tokens for tokens
     */
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);

    /**
     * @notice Swap tokens for exact tokens
     */
    function swapTokensForExactTokens(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);

    /**
     * @notice Swap exact ETH for tokens
     */
    function swapExactETHForTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable returns (uint[] memory amounts);

    /**
     * @notice Swap exact tokens for ETH
     */
    function swapExactTokensForETH(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);

    /**
     * @notice Quote fonksiyonu
     */
    function quote(uint amountA, uint reserveA, uint reserveB) external pure returns (uint amountB);

    /**
     * @notice Get amount out
     */
    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) external pure returns (uint amountOut);

    /**
     * @notice Get amount in
     */
    function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) external pure returns (uint amountIn);

    /**
     * @notice Get amounts out
     */
    function getAmountsOut(uint amountIn, address[] calldata path) external view returns (uint[] memory amounts);

    /**
     * @notice Get amounts in
     */
    function getAmountsIn(uint amountOut, address[] calldata path) external view returns (uint[] memory amounts);
}

/**
 * @title ISushiSwapV2Pair
 * @notice SushiSwap V2 Pair interface'i
 */
interface ISushiSwapV2Pair {
    function token0() external view returns (address);
    function token1() external view returns (address);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function price0CumulativeLast() external view returns (uint);
    function price1CumulativeLast() external view returns (uint);
    function kLast() external view returns (uint);
    function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external;
    function skim(address to) external;
    function sync() external;
}

// ============ Curve Finance Interfaces ============

/**
 * @title ICurveRegistry
 * @notice Curve Registry interface'i
 * @dev Pool discovery ve metadata için kullanılır
 */
interface ICurveRegistry {
    function pool_count() external view returns (uint256);
    function pool_list(uint256 i) external view returns (address);
    function get_pool_from_lp_token(address lp_token) external view returns (address);
    function get_lp_token(address pool) external view returns (address);
    
    /**
     * @notice Pool'daki coin'leri döndürür
     */
    function get_coins(address pool) external view returns (address[8] memory);
    
    /**
     * @notice Pool'daki coin sayısını döndürür
     */
    function get_n_coins(address pool) external view returns (uint256[2] memory);
    
    /**
     * @notice Coin balance'larını döndürür
     */
    function get_balances(address pool) external view returns (uint256[8] memory);
    
    /**
     * @notice Underlying coin'leri döndürür (wrapped için)
     */
    function get_underlying_coins(address pool) external view returns (address[8] memory);
    
    /**
     * @notice Exchange amount'u hesaplar
     */
    function get_exchange_amount(
        address pool,
        address from,
        address to,
        uint256 amount
    ) external view returns (uint256);
}

/**
 * @title ICurvePool
 * @notice Genel Curve pool interface'i
 * @dev Farklı pool tipleri için temel fonksiyonlar
 */
interface ICurvePool {
    /**
     * @notice İki coin arasında exchange yapar
     * @param i Input coin index
     * @param j Output coin index
     * @param dx Input amount
     * @param min_dy Minimum output amount
     * @return Actual output amount
     */
    function exchange(
        int128 i,
        int128 j,
        uint256 dx,
        uint256 min_dy
    ) external returns (uint256);
    
    /**
     * @notice Underlying coin'lerle exchange (wrapped pool'lar için)
     */
    function exchange_underlying(
        int128 i,
        int128 j,
        uint256 dx,
        uint256 min_dy
    ) external returns (uint256);
    
    /**
     * @notice Exchange amount'u hesaplar
     */
    function get_dy(
        int128 i,
        int128 j,
        uint256 dx
    ) external view returns (uint256);
    
    /**
     * @notice Underlying için exchange amount'u hesaplar
     */
    function get_dy_underlying(
        int128 i,
        int128 j,
        uint256 dx
    ) external view returns (uint256);
    
    /**
     * @notice Coin adresini döndürür
     */
    function coins(uint256 index) external view returns (address);
    
    /**
     * @notice Coin balance'ını döndürür
     */
    function balances(uint256 index) external view returns (uint256);
    
    /**
     * @notice Fee'yi döndürür
     */
    function fee() external view returns (uint256);
    
    /**
     * @notice Admin fee'yi döndürür
     */
    function admin_fee() external view returns (uint256);
}

/**
 * @title ICurveStableSwap
 * @notice Curve StableSwap pool interface'i (3pool, sUSD, etc.)
 */
interface ICurveStableSwap is ICurvePool {
    function A() external view returns (uint256);
    function A_precise() external view returns (uint256);
    function get_virtual_price() external view returns (uint256);
    function calc_token_amount(uint256[3] calldata amounts, bool is_deposit) external view returns (uint256);
    function add_liquidity(uint256[3] calldata amounts, uint256 min_mint_amount) external returns (uint256);
    function remove_liquidity(uint256 _amount, uint256[3] calldata min_amounts) external returns (uint256[3] memory);
    function remove_liquidity_imbalance(uint256[3] calldata amounts, uint256 max_burn_amount) external returns (uint256);
    function remove_liquidity_one_coin(uint256 _token_amount, int128 i, uint256 min_amount) external returns (uint256);
}

/**
 * @title ICurveCryptoSwap
 * @notice Curve CryptoSwap pool interface'i (tricrypto, etc.)
 */
interface ICurveCryptoSwap is ICurvePool {
    function token() external view returns (address);
    function price_scale() external view returns (uint256);
    function price_oracle() external view returns (uint256);
    function last_prices() external view returns (uint256);
    function last_prices_timestamp() external view returns (uint256);
    function initial_A_gamma() external view returns (uint256, uint256);
    function future_A_gamma() external view returns (uint256, uint256);
}

// ============ 1inch Interfaces ============

/**
 * @title IAggregationRouterV5
 * @notice 1inch Aggregation Router V5 interface'i
 * @dev En güncel 1inch router versiyonu
 */
interface IAggregationRouterV5 {
    /**
     * @notice Swap description struct
     */
    struct SwapDescription {
        address srcToken;
        address dstToken;
        address srcReceiver;
        address dstReceiver;
        uint256 amount;
        uint256 minReturnAmount;
        uint256 flags;
    }

    /**
     * @notice Ana swap fonksiyonu
     * @param executor Executor adresi (genelde router)
     * @param desc Swap açıklaması
     * @param permit Permit data (opsiyonel)
     * @param data Executor'a gönderilecek data
     * @return returnAmount Alınan miktar
     * @return spentAmount Harcanan miktar
     */
    function swap(
        address executor,
        SwapDescription calldata desc,
        bytes calldata permit,
        bytes calldata data
    ) external payable returns (uint256 returnAmount, uint256 spentAmount);

    /**
     * @notice Swap with minimal parameters
     */
    function unoswap(
        address srcToken,
        uint256 amount,
        uint256 minReturn,
        uint256[] calldata pools
    ) external payable returns (uint256 returnAmount);

    /**
     * @notice Multiple unoswaps
     */
    function unoswapMulti(
        address srcToken,
        uint256[] calldata amounts,
        uint256 minReturn,
        uint256[][] calldata pools
    ) external payable returns (uint256 returnAmount);

    /**
     * @notice Clipper swap için özel fonksiyon
     */
    function clipperSwap(
        address clipperExchange,
        address srcToken,
        address dstToken,
        uint256 inputAmount,
        uint256 outputAmount,
        uint256 goodUntil,
        bytes32 r,
        bytes32 vs
    ) external payable returns (uint256 returnAmount);

    /**
     * @notice RFQ (Request for Quote) swap
     */
    function fillOrderRFQ(
        uint256 info,
        address makerAsset,
        address takerAsset,
        address maker,
        address allowedSender,
        uint256 makingAmount,
        uint256 takingAmount
    ) external payable returns (uint256, uint256);
}

/**
 * @title IAggregationExecutor
 * @notice 1inch Aggregation Executor interface'i
 */
interface IAggregationExecutor {
    /**
     * @notice Execute swap logic
     */
    function execute(address msgSender) external payable;
}

// ============ Balancer V2 Interfaces ============

/**
 * @title IBalancerVault
 * @notice Balancer V2 Vault interface'i
 * @dev Tüm Balancer swap ve likidite işlemleri Vault üzerinden yapılır
 */
interface IBalancerVault {
    /**
     * @notice Swap türleri
     */
    enum SwapKind { GIVEN_IN, GIVEN_OUT }

    /**
     * @notice Single swap struct
     */
    struct SingleSwap {
        bytes32 poolId;
        SwapKind kind;
        address assetIn;
        address assetOut;
        uint256 amount;
        bytes userData;
    }

    /**
     * @notice Batch swap struct
     */
    struct BatchSwapStep {
        bytes32 poolId;
        uint256 assetInIndex;
        uint256 assetOutIndex;
        uint256 amount;
        bytes userData;
    }

    /**
     * @notice Fund management struct
     */
    struct FundManagement {
        address sender;
        bool fromInternalBalance;
        address payable recipient;
        bool toInternalBalance;
    }

    /**
     * @notice Join/Exit türleri
     */
    enum PoolSpecialization { GENERAL, MINIMAL_SWAP_INFO, TWO_TOKEN }

    /**
     * @notice Single swap
     */
    function swap(
        SingleSwap memory singleSwap,
        FundManagement memory funds,
        uint256 limit,
        uint256 deadline
    ) external payable returns (uint256);

    /**
     * @notice Batch swap
     */
    function batchSwap(
        SwapKind kind,
        BatchSwapStep[] memory swaps,
        address[] memory assets,
        FundManagement memory funds,
        int256[] memory limits,
        uint256 deadline
    ) external payable returns (int256[] memory);

    /**
     * @notice Query batch swap (simülasyon için)
     */
    function queryBatchSwap(
        SwapKind kind,
        BatchSwapStep[] memory swaps,
        address[] memory assets,
        FundManagement memory funds
    ) external returns (int256[] memory);

    /**
     * @notice Flash loan
     */
    function flashLoan(
        address recipient,
        address[] memory tokens,
        uint256[] memory amounts,
        bytes memory userData
    ) external;

    /**
     * @notice Get pool tokens
     */
    function getPoolTokens(bytes32 poolId)
        external
        view
        returns (
            address[] memory tokens,
            uint256[] memory balances,
            uint256 lastChangeBlock
        );

    /**
     * @notice Get pool info
     */
    function getPool(bytes32 poolId)
        external
        view
        returns (address, PoolSpecialization);

    /**
     * @notice Join pool
     */
    function joinPool(
        bytes32 poolId,
        address sender,
        address recipient,
        JoinPoolRequest memory request
    ) external payable;

    /**
     * @notice Exit pool
     */
    function exitPool(
        bytes32 poolId,
        address sender,
        address payable recipient,
        ExitPoolRequest memory request
    ) external;

    struct JoinPoolRequest {
        address[] assets;
        uint256[] maxAmountsIn;
        bytes userData;
        bool fromInternalBalance;
    }

    struct ExitPoolRequest {
        address[] assets;
        uint256[] minAmountsOut;
        bytes userData;
        bool toInternalBalance;
    }

    /**
     * @notice Get protocol fees
     */
    function getProtocolFeesCollector() external view returns (address);

    /**
     * @notice Pause state
     */
    function getPausedState()
        external
        view
        returns (
            bool paused,
            uint256 pauseWindowEndTime,
            uint256 bufferPeriodEndTime
        );
}

/**
 * @title IBalancerPool
 * @notice Genel Balancer pool interface'i
 */
interface IBalancerPool {
    function getPoolId() external view returns (bytes32);
    function getVault() external view returns (address);
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function getInvariant() external view returns (uint256);
    function getLastInvariant() external view returns (uint256);
    function getSwapFeePercentage() external view returns (uint256);
    function getScalingFactors() external view returns (uint256[] memory);
    
    /**
     * @notice Calculate amounts out for BPT in
     */
    function calcTokensOutGivenExactBptIn(
        uint256 bptAmountIn
    ) external view returns (uint256[] memory);
    
    /**
     * @notice Calculate BPT out for exact tokens in
     */
    function calcBptOutGivenExactTokensIn(
        uint256[] memory amountsIn
    ) external view returns (uint256);
}

/**
 * @title IBalancerWeightedPool
 * @notice Weighted pool interface'i
 */
interface IBalancerWeightedPool is IBalancerPool {
    function getNormalizedWeights() external view returns (uint256[] memory);
    function getGradualWeightUpdateParams()
        external
        view
        returns (
            uint256 startTime,
            uint256 endTime,
            uint256[] memory startWeights,
            uint256[] memory endWeights
        );
}

/**
 * @title IBalancerStablePool
 * @notice Stable pool interface'i
 */
interface IBalancerStablePool is IBalancerPool {
    function getAmplificationParameter()
        external
        view
        returns (
            uint256 value,
            bool isUpdating,
            uint256 precision
        );
}

// ============ Common DEX Interfaces ============

/**
 * @title IWETH9
 * @notice Wrapped ETH interface'i
 * @dev Tüm DEX'lerde kullanılan WETH9 kontratı için
 */
interface IWETH9 {
    function deposit() external payable;
    function withdraw(uint256 wad) external;
    function totalSupply() external view returns (uint256);
    function approve(address guy, uint256 wad) external returns (bool);
    function transfer(address dst, uint256 wad) external returns (bool);
    function transferFrom(address src, address dst, uint256 wad) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}

/**
 * @title IERC20Extended
 * @notice Extended ERC20 interface with metadata
 */
interface IERC20Extended {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    
    // Metadata extensions
    function name() external view returns (string memory);
    function symbol() external view returns (string memory);
    function decimals() external view returns (uint8);
    
    // EIP-2612 permit
    function permit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;
    
    function nonces(address owner) external view returns (uint256);
    function DOMAIN_SEPARATOR() external view returns (bytes32);
}

